naturalWidth = 800;
naturalHeight = 720;
scale = 1.0;
strScale = "" + scale;
width = parseInt(scale * naturalWidth);
height = parseFloat(scale * naturalHeight);
strWidth = "" + width;
strHeight = "" + height;

textX = 50;
textY1 = 70;
textDY = 45;

bgColor1 = "#ea6486";
bgColor2 = "#cccccc";
bgColor3 = "#87c26c";
bgColor4 = "#9ec5c3";
bgColor5 = "#dceaf0";
bgColor6 = "#9eaac5";
bgColor7 = "#f29976";

getById = function(id) {
    return document.getElementById(id);
};

showHTML = function(id) {
    el = getById(id);
    el.setAttribute("class","notHidden");
};
hideHTML = function(id) {
    el = getById(id);
    el.setAttribute("class","hidden");
};

setColors = function() {
    getById("bgColor1Label").style.backgroundColor = bgColor1;
    getById("bgColor2Label").style.backgroundColor = bgColor2;
    getById("bgColor3Label").style.backgroundColor = bgColor3;
    getById("bgColor4Label").style.backgroundColor = bgColor4;
    getById("bgColor5Label").style.backgroundColor = bgColor5;
    getById("bgColor6Label").style.backgroundColor = bgColor6;
    getById("bgColor7Label").style.backgroundColor = bgColor7;
};

getColor = function() {
    if (getById("bgColor1").checked) {
        return bgColor1;
    }
    else if (getById("bgColor2").checked) {
        return bgColor2;
    }
    else if (getById("bgColor3").checked) {
        return bgColor3;
    }
    else if (getById("bgColor4").checked) {
        return bgColor4;
    }
    else if (getById("bgColor5").checked) {
        return bgColor5;
    }
    else if (getById("bgColor6").checked) {
        return bgColor6;
    }
    else {
        return bgColor7;
    }
};

getAlien = function() {
    if (getById("alien1").checked) {
        return alien1svg;
    }
    else if (getById("alien2").checked) {
        return alien2svg;
    }
    else if (getById("alien3").checked) {
        return alien3svg;
    }
    else if (getById("alien4").checked) {
        return alien4svg;
    }
    else if (getById("alien5").checked) {
        return alien5svg;
    }
    else if (getById("alien6").checked) {
        return alien6svg;
    }
    else if (getById("alien7").checked) {
        return alien7svg;
    }
};

getTextSVG = function(text, yIndex) {
    textY = textY1 + yIndex * textDY;
    t = svgTextTemplate.replace("X--GOES--HERE", textX);
    t = t.replace("Y--GOES--HERE", textY);
    t = t.replace("TEXT--GOES--HERE", text);
    return t;
}

getSVG = function() {
    svg = svgTemplate;
    color = getColor();
    alienSVG = getAlien();

    textSVG = "";
    text1 = getById("text1").value;
    textSVG += getTextSVG(text1, 0);

    text2 = getById("text2").value;
    textSVG += getTextSVG(text2, 1);

    text3 = getById("text3").value;
    textSVG += getTextSVG(text3, 2);

    text4 = getById("text4").value;
    textSVG += getTextSVG(text4, 3);

    //text5 = getById("text5").value;
    //textSVG += getTextSVG(text5, 4);

    svg = svg.replace("TEXT--GOES--HERE",textSVG);
    svg = svg.replace("FILL--GOES--HERE",color);
    svg = svg.replace("ALIEN--GOES--HERE",alienSVG);
    svg = svg.replace("HEIGHT--GOES--HERE",strHeight);
    svg = svg.replace("WIDTH--GOES--HERE",strWidth);
    svg = svg.replace("SCALE--FACTOR--GOES--HERE",strScale);
    return svg;
};

getDescription = function() {
    t1 = getById("text1").value;
    t2 = getById("text2").value;
    t3 = getById("text3").value;
    t4 = getById("text4").value;
    //t5 = getById("text5").value;
    //t6 = getById("text6").value;
    text = t1 + " " + t2 + " " + t3 + " " + t4; // + " " + t5 + " " + t6;
    template = 'Image description: A cartoon alien says: TEXT--GOES--HERE';
    desc = template.replace("TEXT--GOES--HERE",text);
    return desc;
};

generateImage = function() {
    clearImage();
    rawSVG = getSVG();
    getById("hiddenDiv").innerHTML = rawSVG;
    svg = hiddenDiv.childNodes[0];

    if (getById("imgFormatPNG").checked) {
        pxWidth = strWidth + "px";
        pxHeight = strHeight + "px";
        var svgData = new XMLSerializer().serializeToString(svg);
        var canvas = getById("hiddenCanvas");
        canvas.setAttribute("width", pxWidth);
        canvas.setAttribute("height", pxHeight);
        var ctx = canvas.getContext( "2d" );
        ctx.clearRect(0, 0, width, height);                  
        var img = document.createElement("img");
        img.setAttribute("width", pxWidth);
        img.setAttribute("height", pxHeight);
        img.setAttribute( "src", "data:image/svg+xml;base64," + btoa( svgData ) );
        img.onload = function() {
            ctx.drawImage(img, 0, 0, width, height);
            var myImage = new Image();
            myImage.setAttribute("id", "myImage");
            myImage.setAttribute("width", pxWidth);
            myImage.setAttribute("height", pxHeight);
            document.getElementById("imageGoesHere").appendChild(myImage);
            myImage.src = canvas.toDataURL("image/png");
        };
    }
    else {
        var myImage = new Image();
        myImage.setAttribute("id", "myImage");
        myImage.src = 'data:image/svg+xml;utf8,' + encodeURIComponent(rawSVG);
        document.getElementById("imageGoesHere").appendChild(myImage);
    }

    desc = getDescription();
    getById("imgDescription").textContent = desc;

    // Show the social media div
    showHTML("socialMedia");
    showHTML("howToSaveWrapper");
};

clearImage = function() {
    // Remove the image from the DOM
    el = getById("myImage");
    if (el) {
        el.remove();
    }
    // Hide the social media div
    hideHTML("socialMedia");
    hideHTML("howToSaveWrapper");

};

init = function() {
    setColors();    

    // Event listener fires if you click anywhere in the document
    document.addEventListener("click", function(e) {
        var id = e.target.id;

        if (id == "generateImage") {
            generateImage();
        }
        else if (id.slice(0,5) == "alien") {
            clearImage();
        }
        else if (id.slice(0,7) == "bgColor") {
            clearImage();
        }
        else if (id.slice(0,4) == "text") {
            clearImage();
        }
        else if (id.slice(0,9) == "imgFormat") {
            clearImage();
        }
    }, false);
};

