COMIC.svg = {

    wordBubble: function(leftOrRight, numLines, style) {
        if (style == "none" || numLines == 0) {
            return "";
        }
        var bubbleHeight = COMIC.constants.bubbleHeights[numLines];
        var svg = "<rect ";
        svg += COMIC.constants.bubbleStyle;
        svg += " x='";
        if (leftOrRight == 'left') {
            svg += COMIC.constants.bubble1X;
        }
        else {
            svg += COMIC.constants.bubble2X;
        }
        svg += "' y='";
        svg += COMIC.constants.bubbleY;
        svg += "' width='";
        svg += COMIC.constants.bubbleWidth;
        svg += "' height='";
        svg += bubbleHeight;
        svg += "' rx='";
        svg += COMIC.constants.bubbleCornerRadius;
        svg += "' ry='";
        svg += COMIC.constants.bubbleCornerRadius;
        svg += "' />";
        if (style == "word") {
            var stemY1 = COMIC.constants.bubbleY + bubbleHeight - 2;
            if (leftOrRight == "left") {
                var stemX1 = COMIC.constants.bubble1X + 10;
                svg += "<path " + COMIC.constants.bubbleStyle + " d='M " + stemX1 + ","  + stemY1 + " l 10,22 l 2,-22' />";
            }
            else {
                var stemX1 = COMIC.constants.panelWidth/2 + COMIC.constants.bubble1X + COMIC.constants.bubbleWidth - 10;
                svg += "<path " + COMIC.constants.bubbleStyle + " d='M " + stemX1 + ","  + stemY1 + " l -10,22 l -2,-22' />";
            }
        }
        else if (style == "thought") {            
            var X1 = COMIC.constants.bubble1X + 30;
            var Y1 = COMIC.constants.bubbleY + 140;
            var X2 = COMIC.constants.bubble1X + 20;
            var Y2 = COMIC.constants.bubbleY + 120;
            var X3 = COMIC.constants.bubble1X + 15;
            var Y3 = COMIC.constants.bubbleY + 95;
            var X4 = COMIC.constants.bubble1X + 12;
            var Y4 = COMIC.constants.bubbleY + 65;
            var X5 = COMIC.constants.bubble1X + 15;
            var Y5 = COMIC.constants.bubbleY + 35;
            var X6 = COMIC.constants.bubble1X + 10;
            var Y6 = COMIC.constants.bubbleY + bubbleHeight;

            if (leftOrRight == "right") {
                X1 = COMIC.constants.bubble2X + COMIC.constants.bubbleWidth - 30;
                X2 = X1 + 10;
                X3 = X1 + 15;
                X4 = X1 + 18;
                X5 = X1 + 15;
                X6 = X1 + 20;
            }
            svg += "<ellipse " + COMIC.constants.bubbleStyle + " rx='3' ry='2.7' cx='" + X1 + "' cy='" + Y1 + "'/>";
            if (numLines < 6) {
                svg += "<ellipse " + COMIC.constants.bubbleStyle + " rx='5' ry='4.6' cx='" + X2 + "' cy='" + Y2 + "'/>";
            }
            if (numLines < 4) {
                svg += "<ellipse " + COMIC.constants.bubbleStyle + " rx='6' ry='5.4' cx='" + X3 + "' cy='" + Y3 + "'/>";
            }
            if (numLines < 3) {
                svg += "<ellipse " + COMIC.constants.bubbleStyle + " rx='8' ry='7.2' cx='" + X4 + "' cy='" + Y4 + "'/>";
            }
            if (numLines == 3 || numLines == 4 || numLines == 5 || numLines == 6 || numLines == 7) {
                svg += "<ellipse " + COMIC.constants.bubbleStyle + " rx='8' ry='7.2' cx='" + X6 + "' cy='" + Y6 + "'/>";
            }
       }
        return svg;
    },

    getCharacter(i) {
        var c = COMIC.characters[i];

        if (c.hasFaces) {

            if (c.isRobot) {
                var svg = ROBOT;
                svg = svg.replace(/SKIN-COLOR/g, c.skinColor);
            }
            else if (c.isWheels) {
                var svg = WHEELS1;
                svg += WHEELS2;
                svg = svg.replace(/SKIN-COLOR/g, c.skinColor);
                svg = svg.replace(/SHIRT-COLOR/g, c.shirtColor);
                svg = svg.replace(/TROUSER-COLOR/g, c.trouserColor);
                svg = svg.replace(/SHOE-COLOR/g, c.shoeColor);
            }
            else if (c.isSquid) {
                var svg = SQUIDALIEN;
            } 
            else {
                var svg = "";

                if (c.beforeBits) {
                    for (var x=0; x<c.beforeBits.length; x++) {
                        svg += COMIC.svg.getBeforeBit(c.beforeBits[x]);
                    }
                }
                var svg2 = BASE_CHARACTER;
                svg2 = svg2.replace(/SKIN-COLOR/g, c.skinColor);
                svg2 = svg2.replace(/SHIRT-COLOR/g, c.shirtColor);
                svg2 = svg2.replace(/TROUSER-COLOR/g, c.trouserColor);
                svg2 = svg2.replace(/SHOE-COLOR/g, c.shoeColor);
                svg += svg2;
            }
            if (c.extraBits) {
                for (var x=0; x<c.extraBits.length; x++) {
                    svg += COMIC.svg.getExtraBit(c.extraBits[x]);
                }
            }
            return svg;
        }
        else {
            return c.svg;
        }
    },

    previewTop: function() {
        var svg = "<svg xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' width='WIDTH' height='HEIGHT'><rect fill='white' stroke='none' x='0' y='0' width='WIDTH' height='HEIGHT' />";
        svg = svg.replace(/WIDTH/g, COMIC.constants.panelWidth);
        svg = svg.replace(/HEIGHT/g, COMIC.constants.panelHeight);
        return svg;
    },

    top: function() {
        var svg = "<svg xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' width='WIDTH' height='HEIGHT'><rect fill='white' stroke='none' x='0' y='0' width='WIDTH' height='HEIGHT'/>";
        var w = COMIC.model.comicWidth();
        var h = COMIC.model.comicHeight();
        svg = svg.replace(/WIDTH/g, w);
        svg = svg.replace(/HEIGHT/g, h);
        return svg;
    },

    panel: function(i) {

        var svg = '<g style="stroke:#000000;stroke-width:1.2;stroke-linejoin:round;stroke-linecap:round">';

        // Back rectangle
        svg += "<rect x='0' y='0' width='400' height='266' fill='PANEL-COLOR' stroke='#000000' stroke-width='1' />";
        svg = svg.replace(/PANEL-COLOR/g, COMIC.model.backgroundColor);

        // Character 1
        var temp = "<g transform='translate(X-TRANSFORM, Y-TRANSFORM)'>";
        temp = temp.replace(/X-TRANSFORM/g, COMIC.constants.character1X);
        temp = temp.replace(/Y-TRANSFORM/g, COMIC.constants.characterY);
        svg += temp;

        svg += COMIC.svg.getCharacter(COMIC.model.character1Index);
        if (COMIC.characters[COMIC.model.character1Index].hasFaces) {
            svg += COMIC.svg.getFaceSVG(COMIC.model.panels[i].leftFace);
        }
        svg += "</g>";

        // Character 2
        temp = "<g transform='translate(X-TRANSFORM, Y-TRANSFORM)'>";
        temp = temp.replace(/X-TRANSFORM/g, COMIC.constants.character2X);
        temp = temp.replace(/Y-TRANSFORM/g, COMIC.constants.characterY);
        svg += temp;
        svg += COMIC.svg.getCharacter(COMIC.model.character2Index);
        if (COMIC.characters[COMIC.model.character2Index].hasFaces) {
            svg += COMIC.svg.getFaceSVG(COMIC.model.panels[i].rightFace);
        }
        svg += "</g>";

        // Word bubble 1
        var textChunks = COMIC.utils.textFoo( COMIC.model.panels[i].text1 );
        var numLines = textChunks.length;
        var bubble = COMIC.model.panels[i].bubble1;
        svg += COMIC.svg.wordBubble("left", numLines, bubble);

        for (var j=0; j<numLines; j++) {
            var textY = COMIC.constants.textY + j * COMIC.constants.lineHeight;
            svg += "<text ";
            svg += COMIC.constants.textStyle;
            svg += " x='";
            svg += COMIC.constants.text1X;
            svg += "' y='";
            svg += textY;
            svg += "'>";
            svg += textChunks[j];
            svg += "</text>";
        }

        // Word bubble 2
        textChunks = COMIC.utils.textFoo( COMIC.model.panels[i].text2 );
        numLines = textChunks.length;
        bubble = COMIC.model.panels[i].bubble2;
        svg += COMIC.svg.wordBubble("right", numLines, bubble);

        for (var j=0; j<numLines; j++) {
            var textY = COMIC.constants.textY + j * COMIC.constants.lineHeight;
            svg += "<text ";
            svg += COMIC.constants.textStyle;
            svg += " x='";
            svg += COMIC.constants.text2X;
            svg += "' y='";
            svg += textY;
            svg += "'>";
            svg += textChunks[j];
            svg += "</text>";
        }
        svg += "</g>";
        return svg;
    },

    bottom: function() {
        var y = COMIC.model.madeAtY();
        var bar = "<text font-size='10' fill='grey' x='10' y='";
        bar += y;
        bar += "'>";
        bar += COMIC.constants.bottomText;
        bar += "</text></svg>";
        return bar;
    },

    preview: function() {
        var i = COMIC.model.currentPanelIndex;
        var svg = COMIC.svg.previewTop();
        svg += COMIC.svg.panel(i);
        svg += '</svg>';
        return svg;
    },
    fullComic: function() {
        var svg = COMIC.svg.top();
        var x = COMIC.constants.panelHorizontalSpace;
        numPanels = COMIC.model.panels.length;
        for (var i=0; i<numPanels; i++) {
            var y = COMIC.constants.verticalSpaceAbovePanel*(1+i) + COMIC.constants.panelHeight*i;
            var temp = "<g class='PANEL' transform='translate(X-TRANSFORM,Y-TRANSFORM)'>";
            temp = temp.replace(/X-TRANSFORM/g, x);
            temp = temp.replace(/Y-TRANSFORM/g, y);
            svg += temp;
            svg += COMIC.svg.panel(i);
            svg += "</g>";
        }
        svg += COMIC.svg.bottom();
        return svg;
    },

    devCharacter: function(i) {
        var template = "<svg xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' width='120' height='120'> INSERT </svg>";
        var insert = COMIC.svg.getCharacter(i);
        var svgFoo = template.replace(/INSERT/g, insert);
        return svgFoo;
    },

    devSeeAllCharacters: function()  {
        var n = COMIC.characters.length;
        for (var i=0; i<n; i++) {
            html += "<h2>";
            html += i;
            html += "</h2>";
            html += COMIC.svg.devCharacter(i);
        }
        document.write(html);
    }
};

COMIC.svg.getBeforeBit = function(n) {
    for(var i=0; i<COMIC.svg.beforeBits.length; i++) {
        if (COMIC.svg.beforeBits[i].n == n) {
            return COMIC.svg.beforeBits[i].svg;
        }
    }
};

COMIC.svg.getExtraBit = function(n) {
    for(var i=0; i<COMIC.svg.extraBits.length; i++) {
        if (COMIC.svg.extraBits[i].n == n) {
            return COMIC.svg.extraBits[i].svg;
        }
    }
};

